    // setMyObject(obj);
    // console.log(obj);
  }, []);
  const findCategoryName = (categoryId) => {
    console.log(props.selects);
    let categoryName = props.selects.find((x) => x.id === categoryId).text;
    return "categoryName";
  };
  return (
    <div className="">
      <ol className="a">
        {props.todo.map((todo, index) => (
          <li
            style={{ backgroundColor: todo.completed ? "green" : "red" }}
            key={index}
            className="listItem"
          >
            <div className="telete">
              <span>{todo.text}</span>
              <button
                className="delete"
                key={index}
                onClick={() => deletefunction(index)}
              >
                delete
              </button>
              <div className="kategori">
                <span>{findCategoryName(todo.categoryId)}</span>
              </div>
            </div>
            <div className="kategori"></div>
          </li>
        ))}
      </ol>
    </div>
  );
};
export default Container;
