import React from "react";
import "./Container";
const Listcontainer = (props) => {
  function deletefunction(index) {
    let arr = props.todo.filter((value, i) => {
      return i === index ? null : value;
    });
    props.setTodo(arr);
  }
  const findCategoryName = (categoryId) => {
    let categoryName = props.selects.find((x) => x.id === categoryId).text;
    return categoryName;
  };
  return (
    <li
      style={{ backgroundColor: props.todo.completed ? "green" : "red" }}
      key={props.index}
      className="listItem"
    >
      <div className="telete">
        <span>{props.todo.text}</span>
        <button
          className="delete"
          key={props.index}
          onClick={() => props.deletefunction(props.index)}
        >
          delete
        </button>
        <div className="kategori">
          <span>{props.findCategoryName(props.todo.categoryId)}</span>
        </div>
      </div>
    </li>
  );
};
export default Listcontainer;
