import React from "react";
import "./container.css";
import { useState, useEffect } from "react";

const Container = (props) => {
  function deletefunction(index) {
    let arr = props.todo.filter((value, i) => {
      return i === index ? null : value;
    });
    props.setTodo(arr);
  }
  const findCategoryName = (categoryId) => {
    let categoryName = props.selects.find((x) => x.id === categoryId).text;
    return categoryName;
  };
  return (
    <ol className="a">
      {props.todo.map((todo, index) => (
        <li
          style={{ backgroundColor: todo.completed ? "green" : "red" }}
          key={index}
          className="listItem"
        >
          <div className="telete">
            <span>{todo.text}</span>
            <button
              className="delete"
              key={index}
              onClick={() => deletefunction(index)}
            >
              delete
            </button>
            <div className="kategori">
              <span>{findCategoryName(todo.categoryId)}</span>
            </div>
          </div>
        </li>
      ))}
    </ol>
  );
};
export default Container;
