import React from "react";
import "./Container";
import { useState, useEffect } from "react";
const Listcontainer = (props) => {
  function deletefunction(index) {
    let arr = props.todo.filter((value, i) => {
      return i === index ? null : value;
    });
    props.setTodo(arr);
  }

  const [myObject, setMyObject] = useState([]);
  useEffect(() => {
    let obj = props.selects.find((x) => x.id === props.categoryId).text;
    setMyObject(obj);
  }, []);

  return (
    <li
      style={{ backgroundColor: props.todo.completed ? "green" : "red" }}
      key={props.index}
      className="listItem"
    >
      <div className="telete">
        <span>{props.todo.text}</span>
        <button
          className="delete"
          key={props.index}
          onClick={() => deletefunction(props.index)}
        >
          delete
        </button>
        <div className="kategori">
          <span>{myObject.props.todo.categoryId}</span>
        </div>
      </div>
    </li>
  );
};
export default Listcontainer;
