import "./App.css";
import { useState } from "react";
import "../src/Components/Container";
import Container from "../src/Components/Container";
function App() {
  const [todos, setTodos] = useState([]);
  const [todo, setTodo] = useState("");
  const [isChecked, setIsChecked] = useState(false);

  function IsChanged() {
    setIsChecked(!isChecked);
  }
  function handleSubmit(e) {
    e.preventDefault();
    const newTodo = {
      text: todo,
      completed: isChecked,
    };
    setTodos([...todos, newTodo]);
    setTodo("");
  }

  return (
    <div className="App">
      <header className="Header">To Do List</header>
      <form className="form">
        <div className="cekput">
          <input
            type="text"
            className="input"
            value={todo}
            placeholder="Enter a new todo"
            onChange={(e) => setTodo(e.target.value)}
          ></input>
          <input type="checkbox" className="cek" onChange={IsChanged} />

          <input
            className="categories"
            value={todo}
            placeholder="Enter a Categories"
            onChange={(e) => setTodo(e.target.value)}
            type="text"
          ></input>
        </div>
        <button type="submit" className="buton" onClick={handleSubmit}>
          Do It
        </button>
      </form>
      <Container setTodo={setTodos} todo={todos} />
    </div>
  );
}

export default App;
