import "./App.css";
import { useState, useEffect, useParams } from "react";
import "../src/Components/Container";
import Container from "../src/Components/Container";
function App() {
  const [todos, setTodos] = useState([]);
  const [todo, setTodo] = useState("");
  const [isChecked, setIsChecked] = useState(false);
  const [selects, setSelects] = useState("");
  const [select, setSelect] = useState([]);
  const [selectedValue, setSelectedValue] = useState("");
  function IsChanged() {
    setIsChecked(!isChecked);
  }
  function handleSubmit(e) {
    let { id } = useParams();
    e.preventDefault();
    const newTodo = {
      text: todo,
      categoryId: selectedValue,
      completed: isChecked,
    };
    setTodos([...todos, newTodo]);
    setTodo("");
  }

  function addCategory(e) {
    e.preventDefault();
    const newCategory = {
      text: selects,
      id: select.length === 0 ? 1 : select[select.length - 1].id + 1,
    };

    if (select.length === 0) {
      setSelectedValue(1);
    }
    setSelect([...select, newCategory]);
    setSelects("");
  }

  return (
    <div className="App">
      <header className="Header">To Do List</header>
      <form className="form">
        <div className="addcategories">
          <input
            onChange={(e) => setSelects(e.target.value)}
            type="text"
            value={selects}
            className="input2"
          />
          <button onClick={addCategory}>add category</button>
        </div>
        <div className="cekput">
          <input
            type="text"
            className="input"
            value={todo}
            placeholder="Enter a new todo"
            onChange={(e) => setTodo(e.target.value)}
          ></input>
          <input type="checkbox" className="cek" onChange={IsChanged} />
        </div>
        <button type="submit" className="buton" onClick={handleSubmit}>
          Do It
        </button>
        <select
          className="categories"
          name="categories"
          onChange={(a) => setSelectedValue(a.target.value)}
        >
          {select.map((selects) => (
            <option key={selects.id} value={selects.id} id={"my-element"}>
              {selects.text}
            </option>
          ))}
        </select>
      </form>

      <Container setTodo={setTodos} todo={todos} />
    </div>
  );
}

export default App;
