import "./App.css";
import { useState } from "react";
import "../src/Components/Container";
import Container from "../src/Components/Container";
function App() {
  const [todos, setTodos] = useState([]);
  const [todo, setTodo] = useState("");
  const [isChecked, setIsChecked] = useState(false);
  const [select, setSelect] = useState([
    {
      id: 1,
      text: "sport",
    },
    {
      id: 2,
      text: "school",
    },
    {
      id: 3,
      text: "work",
    },
    {
      id: 4,
      text: "shopping",
    },
    {
      id: 5,
      text: "love",
    },
  ]);

  function IsChanged() {
    setIsChecked(!isChecked);
  }
  function handleSubmit(e) {
    e.preventDefault();
    const newTodo = {
      text: todo,
      completed: isChecked,
    };
    setTodos([...todos, newTodo]);
    setTodo("");
  }
  function addCategory(e) {
    e.preventDefault();
    const newCategory = {
      text: select,
    };
  }

  return (
    <div className="App">
      <header className="Header">To Do List</header>
      <form className="form">
        <div className="addcategories">
          <input type="text" className="input2" />
          <button onChange={(e) => setSelect(e.target.value)}>
            add category
          </button>
          <select className="categories" name="categories" id="categories">
            <option value="spor">sports</option>
            <option value="school">school</option>
            <option value="work">work</option>
            <option value="shopping">shop</option>
          </select>
        </div>
        <div className="cekput">
          <input
            type="text"
            className="input"
            value={todo}
            placeholder="Enter a new todo"
            onChange={(e) => setTodo(e.target.value)}
          ></input>
          <input type="checkbox" className="cek" onChange={IsChanged} />
        </div>
        <button type="submit" className="buton" onClick={handleSubmit}>
          Do It
        </button>
      </form>
      <Container setTodo={setTodos} todo={todos} />
    </div>
  );
}

export default App;
