import React from "react";
import "./Container";
import { useState, useEffect } from "react";
const Listcontainer = (props) => {
  const { index, todo, selects, setTodo } = props;

  function deletefunction(index) {
    let arr = todo.filter((value, i) => {
      return i === index ? null : value;
    });
    setTodo(arr);
  }

  console.log("todo", selects);

  return (
    <li
      style={{ backgroundColor: todo.completed ? "green" : "red" }}
      key={index}
      className="listItem"
    >
      <div className="telete">
        <span>{todo.text}</span>
        <button
          className="delete"
          key={index}
          onClick={() => deletefunction(index)}
        >
          delete
        </button>
        <div className="kategori">
          <span>{selects.find((x) => x.id == todo.categoryId).text}</span>
        </div>
      </div>
    </li>
  );
};
export default Listcontainer;
